import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import AnimatedBg from './components/AnimatedBg';
import Particles from './components/Particles';
import LoginPage from './components/LoginPage';
import MainPanel from './components/MainPanel';
import Analytics from './components/Analytics';
import Insights from './components/Insights';
import './App.css';

const API_URL = 'http://localhost:5000/api/tasks';

function App() {
  const [isAuth, setIsAuth] = useState(!!localStorage.getItem('taskflow_auth'));
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch tasks from backend
  const fetchTasks = async () => {
    setLoading(true);
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      setTasks(data);
    } catch (err) {
      alert('Failed to fetch tasks from backend.');
    }
    setLoading(false);
  };

  // Auth logic
  const loginWithGoogle = () => {
    localStorage.setItem('taskflow_auth', 'true');
    setIsAuth(true);
  };
  const emailSignIn = (email, password) => {
    // Simulate login (in real app, call backend)
    localStorage.setItem('taskflow_auth', 'true');
    setIsAuth(true);
  };
  const logout = () => {
    localStorage.removeItem('taskflow_auth');
    setIsAuth(false);
  };

  useEffect(() => {
    if (isAuth) fetchTasks();
  }, [isAuth]);

  return (
    <div>
      <AnimatedBg />
      <Particles />
      {!isAuth ? (
        <LoginPage onLogin={loginWithGoogle} onEmailSignIn={emailSignIn} />
      ) : (
        <Router>
          <div className="container" id="mainApp">
            <div className="main-panel">
              <div className="header">
                <h1>TaskFlow Analytics</h1>
                <p>Transform your productivity with smart insights 🚀</p>
              </div>
              <div className="nav-tabs">
                <Link to="/" className="nav-tab">📋 Tasks</Link>
                <Link to="/analytics" className="nav-tab">📊 Analytics</Link>
                <Link to="/insights" className="nav-tab">💡 Insights</Link>
              </div>
              <Routes>
                <Route path="/" element={<MainPanel tasks={tasks} setTasks={setTasks} fetchTasks={fetchTasks} logout={logout} loading={loading} />} />
                <Route path="/analytics" element={<Analytics tasks={tasks} />} />
                <Route path="/insights" element={<Insights tasks={tasks} />} />
                <Route path="*" element={<Navigate to="/" />} />
              </Routes>
            </div>
          </div>
        </Router>
      )}
    </div>
  );
}

export default App;
