import React, { useState } from 'react';
import TaskInput from './TaskInput';
import StatsGrid from './StatsGrid';
import FilterTabs from './FilterTabs';
import TasksContainer from './TasksContainer';
import Insights from './Insights';
import Analytics from './Analytics';

const API_URL = 'http://localhost:5000/api/tasks';

const MainPanel = ({ tasks, setTasks, fetchTasks, logout, loading }) => {
  const [currentFilter, setCurrentFilter] = useState('all');
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [addLoading, setAddLoading] = useState(false);
  const [editLoading, setEditLoading] = useState(false);
  const [view, setView] = useState('dashboard'); // 'dashboard' | 'insights' | 'analytics'

  // Add task
  const handleAdd = async ({ title, category }) => {
    setAddLoading(true);
    try {
      await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title, category, status: 'pending', createdAt: new Date().toISOString() })
      });
      await fetchTasks();
    } catch (err) {
      alert('Failed to add task.');
    }
    setAddLoading(false);
  };

  // Edit task (show input)
  const handleEdit = (id, value) => {
    setEditingTaskId(id);
    setEditValue(value);
  };

  // Save edited task
  const handleSaveEdit = async (id) => {
    if (!editValue.trim()) return;
    const task = tasks.find(t => t._id === id);
    if (!task) return;
    setEditLoading(true);
    try {
      await fetch(`${API_URL}/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...task, title: editValue.trim() })
      });
      setEditingTaskId(null);
      setEditValue('');
      await fetchTasks();
    } catch (err) {
      alert('Failed to update task.');
    }
    setEditLoading(false);
  };

  // Cancel edit
  const handleCancelEdit = () => {
    setEditingTaskId(null);
    setEditValue('');
  };

  // Complete/Undo task
  const handleComplete = async (id) => {
    const task = tasks.find(t => t._id === id);
    if (!task) return;
    try {
      await fetch(`${API_URL}/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...task, status: task.status === 'completed' ? 'pending' : 'completed' })
      });
      await fetchTasks();
    } catch (err) {
      alert('Failed to update task status.');
    }
  };

  // Delete task
  const handleDelete = async (id) => {
    try {
      await fetch(`${API_URL}/${id}`, { method: 'DELETE' });
      await fetchTasks();
    } catch (err) {
      alert('Failed to delete task.');
    }
  };

  // Stats
  const today = new Date().toISOString().slice(0, 10);
  const total = tasks.length;
  const completed = tasks.filter(t => t.status === 'completed').length;
  const todayCount = tasks.filter(t => t.createdAt && t.createdAt.slice(0, 10) === today).length;
  const productivity = total === 0 ? 0 : Math.round(100 * completed / total);

  // For editing input
  const handleEditInput = (id, value) => {
    setEditValue(value);
  };

  // For TasksContainer, pass editValue to the editing task
  const tasksWithEdit = editingTaskId
    ? tasks.map(t => t._id === editingTaskId ? { ...t, title: editValue } : t)
    : tasks;

  const navBtnStyle = isActive => ({
    padding: '10px 18px',
    marginRight: 8,
    border: 'none',
    borderRadius: 6,
    background: isActive ? 'linear-gradient(90deg,#4f8cff,#6ed6ff)' : '#f0f4fa',
    color: isActive ? 'white' : '#222',
    fontWeight: isActive ? 700 : 500,
    cursor: 'pointer',
    boxShadow: isActive ? '0 2px 8px rgba(79,140,255,0.15)' : 'none',
    transition: 'all 0.2s',
  });
  return (
    <div style={{maxWidth:900,margin:'0 auto',padding:'32px 16px'}}>
      <div style={{display:'flex',alignItems:'center',justifyContent:'space-between',marginBottom:32}}>
        <h1 style={{margin:0,fontSize:32,letterSpacing:1,fontWeight:800,color:'#4f8cff'}}>TaskFlow Analytics</h1>
        <div>
          <button style={navBtnStyle(view==='dashboard')} onClick={()=>setView('dashboard')}>Dashboard</button>
          <button style={navBtnStyle(view==='insights')} onClick={()=>setView('insights')}>Insights</button>
          <button style={navBtnStyle(view==='analytics')} onClick={()=>setView('analytics')}>Analytics</button>
          <button style={{...navBtnStyle(false),background:'#ff4f4f',color:'white',marginLeft:16}} onClick={logout}>Logout</button>
        </div>
      </div>
      {view === 'dashboard' && (
        <>
          <TaskInput onAdd={handleAdd} loading={addLoading} />
          <StatsGrid total={total} completed={completed} today={todayCount} productivity={productivity} />
          <FilterTabs current={currentFilter} onChange={setCurrentFilter} />
          <TasksContainer
            tasks={tasksWithEdit}
            filter={currentFilter}
            onEdit={handleEdit}
            onEditInput={handleEditInput}
            onDelete={handleDelete}
            onComplete={handleComplete}
            onSaveEdit={handleSaveEdit}
            onCancelEdit={handleCancelEdit}
            editingTaskId={editingTaskId}
          />
        </>
      )}
      {view === 'insights' && (
        <div style={{animation:'fadeIn 0.3s'}}>
          <Insights tasks={tasks} />
        </div>
      )}
      {view === 'analytics' && (
        <div style={{animation:'fadeIn 0.3s'}}>
          <Analytics tasks={tasks} />
        </div>
      )}
    </div>
  );
};

export default MainPanel;
